	jQuery( document ).ready( function () {
	    var moduleContainer = jQuery("#dnn_ctr5055_ModuleContent");
		var slideContainer = moduleContainer.find( ".slideContainer" );
		var pagerContainer = moduleContainer.find( ".pagerContainer" );
        var pagerIcons = pagerContainer.find(".page");
        var slides = slideContainer.children("div.slide");
		function ChopSlideText(obj) {
			var slideTextContainer = obj.closest("div.slideText");
			var originalString = obj.html();
			if (originalString.length > 400) { originalString = originalString.substr(0, 400);
}
			while (slideTextContainer.height() < slideTextContainer[0].scrollHeight && originalString.length > 0) {
				originalString = originalString.substr(0, originalString.length - 5);
				obj.html(originalString + "...");
}
		}
        $.each(slides.children("div.rightSide").children("div.slideText").children("div.slideCaption"), function () {
            ChopSlideText($(this));
});
        slideContainer.cycle({
 			slides: "> .slide",
            fx: "fade",
			speed: 1500,
			timeout: 15000,
	        pauseOnHover: true,
			log:false,
            pager: slideContainer.children("div.pagerContainer"),
			pagerTemplate: "",
			pagerActiveClass: "activeSlide"
	});

	if(false) {
                slideContainer.on('cycle-next', function(event, opts){
                    slideContainer.cycle('pause');
	});
	slideContainer.on('cycle-prev', function(event, opts){
	    slideContainer.cycle('pause');
	});
	}

